import React, { useEffect, useState } from 'react';
import SideMenu from '../FRONT/SECONPAGE/SideMenu'
import "../FRONT/SECONPAGE/SecondPage.css"
import Menubox from './Menubox';
import LoadingPage from "../LoadingPage"

function Menu() {
const [data,setData]=useState([]);
const[isloading,setIsLoading]=useState(true);
const[isError,setIsError]=useState();



const  getdata=()=>{
    fetch("https://www.themealdb.com/api/json/v1/1/search.php?s").
    then((resp)=>resp.json()).
    then((datagroup)=>{

    setData(datagroup.meals)
    console.log(datagroup.meals)
    setIsLoading(false)

    }).catch("error")
}

useEffect(()=>{
  getdata()
},[])
const NewData=data.map((datas)=>{
  return(
    <div key={datas.idMeal}>
   <Menubox 
    Totaldata={datas}
    Name={datas.strMeal} 
    Area={datas.strArea} 
    Type={datas.strTags}
    img={datas.strMealThumb}/>
    </div>
  )
})

  return(
    <div className='menucontainer'> 
     <h1 className="menuhead">Menu</h1>
    <div className='menu' >
      <div menuload>
      {isloading&&<LoadingPage/>}</div>
      { NewData}
      <div className='sidemenu'>
      <SideMenu className="sidemenu-"/>
      </div>
      <div></div>
    </div>
    </div>)
 
}

export default Menu;
