import React, { useState } from 'react';
import "./menu.css"


function MenuDisplay({myprops,tcloseMenu}) {
    const [mytable,setMytable]=useState([])
    console.log(myprops)
    const mytabledata=myprops.map((prop)=>{
        return(
            <table>
            <tr>
                <th>Ingredient</th>
                <th>Measurment</th>
            </tr>
            <tr>
                <td>{prop.strIngredient1}</td>
                <td>{prop.strMeasure1}</td>
            </tr>
            </table>
        )
    })
    setMytable(mytabledata)
  return <div className='menubox' >  
       <h1 className='headname'>{myprops.strMeal}</h1>
       <div >
       <div className='newmenuimg' style={{backgroundImage:`url(${myprops.strMealThumb})`}} ></div>
     </div>
     <div className='table'> 
        {mytable}
     </div>
     <div className='recipe'>
        <h2 className='heading'>Recipe</h2>
       <div  className='recipebox'> <h4 className='instruction'>{myprops.strInstructions}</h4></div>
     </div>
     <input type="submit" value="Close" className="button" onClick={closeMenu}/>
         </div>;
}

export default MenuDisplay;
