import React, { useEffect, useState } from 'react';
import SideMenu from '../FRONT/SECONPAGE/SideMenu'
import "../FRONT/SECONPAGE/SecondPage.css"
import Menubox from './Menubox';
import Errors from "../Errors"
import LoadingPage from "../LoadingPage"
import MenuDisplay from './MenuDisplay';
function Menu() {
const [data,setData]=useState([]);
const[isloading,setIsLoading]=useState(true);
const[isError,setIsError]=useState(false);
const[showMore,setShowMore]=useState("Show more")
const [truthvar,setTruthVar]=useState(53000)

const ShowMore=()=>{
setTruthVar(52000);
setShowMore("")
}

const  getdata=()=>{
    fetch("https://www.themealdb.com/api/json/v1/1/search.php?s").
    then((resp)=>resp.json()).
    then((datagroup)=>{

    setData(datagroup.meals)
    setIsLoading(false)

    }).catch(()=>{
      setIsError(true)
      setIsLoading(false)
    }
    )
}
const getmenu=(id)=>{
  console.log(id)
const menudisplay=data.filter((mydata)=>{
 if(id==mydata){
   return(
    <div>
 <MenuDisplay  myprobs/>
    </div>
  )}
})}

useEffect(()=>{
  getdata()
},[])
const NewData=data.map((datas)=>{
  if(datas.idMeal<truthvar){
    return null;}
  else return(
    <div key={datas.idMeal}  onClick={()=>getmenu(datas.idMeal)}>
   <Menubox 
    Totaldata={datas}
    Name={datas.strMeal} 
    Area={datas.strArea} 
    Type={datas.strTags}
    img={datas.strMealThumb}
    />
    </div>
  )
})

  return(
    <div className='menucontainer'> 
     <h1 className="menuhead" >Menu</h1>
    <div className='menu' >
      <div className="menuloading">
      {isloading&&<LoadingPage/>}
      {isError && <Errors/>}
        </div >
      { NewData  }
      <div className='sidemenu'>
      <SideMenu className="sidemenu-" />
      </div>
      <div>{!isError&&!isloading&&showMore&&<button  onClick={ShowMore}>{showMore}</button>}</div>
    </div>
    </div>)
 
}

export default Menu;
