import React, { useEffect, useState } from 'react';
import SideMenu from '../FRONT/SECONPAGE/SideMenu'
import "../FRONT/SECONPAGE/SecondPage.css"
import Menubox from './Menubox';
import Errors from "../Errors"
import LoadingPage from "../LoadingPage"
import MenuDisplay from './MenuDisplay';
import "./menu.css"
function Menu() {
const [data,setData]=useState([]);
const[isloading,setIsLoading]=useState(true);
const[isError,setIsError]=useState(false);
const[showMore,setShowMore]=useState("Show more")
const [truthvar,setTruthVar]=useState(53000)
const[Menu,setMenu]=useState([])
const[displayMenu,setDisplayMenu]=useState(false)


const ShowMore=()=>{
setTruthVar(52000);
setShowMore("")
}

const  getdata=()=>{
    fetch("https://www.themealdb.com/api/json/v1/1/search.php?s").
    then((resp)=>resp.json()).
    then((datagroup)=>{

    setData(datagroup.meals)
    setIsLoading(false)

    }).catch(()=>{
      setIsError(true)
      setIsLoading(false)
    }
    )
}


useEffect(()=>{
  getdata()
},[])
const NewData=data.map((datas)=>{
  if(datas.idMeal<truthvar){
    return null;}
  else return(
    <div key={datas.idMeal}  onClick={()=>getmenu(datas.idMeal)}>
   <Menubox 
    Totaldata={datas}
    Name={datas.strMeal} 
    Area={datas.strArea} 
    Type={datas.strTags}
    img={datas.strMealThumb}
    />
    </div>
  )
})
const getmenu=(id)=>{
  setDisplayMenu(!displayMenu)
  const Menudisplay=data.filter((mydata)=>{
  if(mydata.idMeal===id){
    return mydata
  }
  
 }).map((mydata)=>{
   return<div key={mydata.idMeal}  className='menuDisplay'>
     <MenuDisplay myprops={mydata} total={data} closeMenu={()=>{setDisplayMenu(false)}}/>
   </div>
 })
setMenu(Menudisplay)}
 
  

  return(
    <div className='menucontainer'> 
     <h1 className="menuhead" >Menu</h1>
    <div className='menu' >
      <div className="menuloading">
      {isloading&&<LoadingPage/>}
      {isError && <Errors/>}
        </div >
      { NewData  }
      {displayMenu && Menu}
      <div className='sidemenu'>
      <SideMenu className="sidemenu-" />
      </div>
      <div>{!isError&&!isloading&&showMore&&<button  onClick={ShowMore}>{showMore}</button>}</div>
    </div>
    </div>)
 
}

export default Menu;
