import React from 'react';
import { useState } from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import Aboutus from '../../ABOUT US/Aboutus';
function SideMenu({className}) {
    const[showabout,setShowabout]=useState(false)
const showAbout=()=>{
setShowabout(true)
}
const linkstyle={
    textDecoration:"none",
    color:"rgb(33 49 64)"
}  

  return(
  
                
                <div className={className}>
                    <div className='sidemenu-head'>
                    <img src="/images/chef-icon.png" width={100}  alt="chef illustration"/>
                    <h3>Mr Chef</h3>
                    <h6>Since 1999</h6>
                    </div>
                    <ul className='sidemenu-body'>
                        <li><Link to="/" style={linkstyle}>Home</Link></li>
                        <li onClick={showAbout}>About Us</li>
                        <li><Link to="/menu" style={linkstyle}>Menu</Link></li>
                        <li>Contact Us</li>
                    </ul>
                    <div className='sidemenu-footer'>
                        <h3>Also Check</h3>
                        <h2><Link to='/'>Le Muse cuisine</Link></h2>
                        <h6>To order delicious delicacies</h6>
                    </div>
                     {showabout&& <Aboutus/>}
                </div>
        
      )}


export default SideMenu;
