import React, { useEffect, useState } from 'react';
import SideMenu from '../FRONT/SECONPAGE/SideMenu'
import "../FRONT/SECONPAGE/SecondPage.css"
import Menubox from './Menubox';
import Errors from "../Errors"
import LoadingPage from "../LoadingPage"

function Menu() {
const [data,setData]=useState([]);
const[isloading,setIsLoading]=useState(true);
const[isError,setIsError]=useState(false);



const  getdata=()=>{
    fetch("https://www.themealdb.com/api/json/v1/1/search.php?s").
    then((resp)=>resp.json()).
    then((datagroup)=>{

    setData(datagroup.meals)
    setIsLoading(false)

    }).catch(()=>{
      setIsError(true)
      setIsLoading(false)
    }
    )
}


useEffect(()=>{
  getdata()
},[])
const NewData=data.map((datas)=>{
  console.log(datas.index)
  if(datas.idMeal<5300){
    return null;}
  else return(
    <div key={datas.idMeal}>
   <Menubox 
    Totaldata={datas}
    Name={datas.strMeal} 
    Area={datas.strArea} 
    Type={datas.strTags}
    img={datas.strMealThumb}/>
    </div>
  )
})

  return(
    <div className='menucontainer'> 
     <h1 className="menuhead">Menu</h1>
    <div className='menu' >
      <div className="menuloading">
      {isloading&&<LoadingPage/>}
      {isError && <Errors/>}
        </div>
      { NewData}
      <div className='sidemenu'>
      <SideMenu className="sidemenu-"/>
      </div>
      <div></div>
    </div>
    </div>)
 
}

export default Menu;
